bool findCombinations_N_length(int length, char *charstring, char *salt, char *hash) {

    printf("Checking passwords of length %d\n", length);

    // Array stores the ascii values of the appropriate characters.
    // Cant be larger than length of 5, according to specification in challenge.
    // Length of array should be max password length + 1.
    int arr[6] = {0};

    // Used to store the actual string, after each char has been converted from ascii.
    // Length of array should be max password length + 1
    char string[6] = {'a'};

    while(1){

        // Convert all numbers into their relevent chars and store in string variable.
        for (int i = 0; i < length; i++) {
            string[(length - i) - 1] = charstring[arr[i]];
        }

        // Check is the hash generated from the string and salt is equal to the supplied hash.
        if ((strcmp(crypt(string, salt),hash) == 0)) {

            printf("%s\n", string);
            return true;
        }

        /*
    Increment relevent number in int array, i.e. if the 5th value in 'arr' is 0, which represents 't', increment it by 1,
        which makes it 1, which represents 'T'.
        This loop loops through all possible combinations.
        */
        for (int i = length; i >= 0; i--) {
            int index = length - i;
            if (i == 0) return false;
            if (arr[index] >= 51) {
                arr[index] = 0;
            } else {
                arr[index] = arr[index] + 1;
                break;
            }

        }
    }
}
