#include <cs50.h>

#include "helpers.h"

/**
 * Returns true if value is in array of n values, else false.
 */
bool search(int value, int values[], int n)
{
    int start = n;
    int target = value;
    int middle;
    int end = 0;
    //Repeat until the (sub)array is of size 0
    do
    {
     //Calculate the middle point of the current (sub)array
     middle = (start + end)/2;
     //If the target is in the middle,...
     if(target == middle)
      {
       //...Stop.
       return true;
      }
     //Otherwise, if the target is less than what's in the middle...
     if(target < middle)
      {
       //...Change the end to just the left of the middle.
       end = middle - 1;
      }
     //Otherwise, if the target is more than what's in the middle...
     if(target > middle)
      {
        //...Change the start to just the right of the middle.
        start = middle + 1;
      }

    }
    while(start - end == 0);
    return false;
}

/**
 * Sorts array of n values.
 */
void sort(int values[], int n)//Note: SC = swap counter
{
    //Temporary Variable
    int temp;
    //Set swap counter to non-zero value (I just chose 7!)
    int SC = 7;
    //Repeat until swap counter is 0:
    do
    {
      //Reset swap counter to 0
      SC = 0;
      //Look at each adjacent pair
      for(int i = 0, h = n - 2; i < h; i++)
       {
           //If two adjacent elements not in order,
           if(values[i] > values[i - 1])
            {             //Swap them...
             temp = values[i - 1];
             values[i - 1] = values[i];
             values[i] = temp;
             //...And add one to the swap counter
             SC++;
            }
       }

    }
    while(SC != 0);
    return;
}```

